mapper.delete:
DELETE
FROM ta_role
WHERE tenant_id = #{tenantId,jdbcType=VARCHAR}
  AND role_id = #{roleId,jdbcType=VARCHAR}

mapper.select:
SELECT *
FROM ta_role
WHERE tenant_id = #{tenantId,jdbcType=VARCHAR}
  AND role_id = #{roleId,jdbcType=VARCHAR}
  AND locale = #{locale,jdbcType=VARCHAR}
LIMIT 1

SELECT *
FROM ta_role
WHERE tenant_id = #{tenantId,jdbcType=VARCHAR}
  AND role_id = #{roleId,jdbcType=VARCHAR}

SELECT *
FROM ta_role
WHERE tenant_id = #{tenantId,jdbcType=VARCHAR}
  AND locale = #{locale,jdbcType=VARCHAR}
