# coding: utf-8

import os
import json


def build_new_instance():
    incident_instance = json.loads(open(os.getenv("varConfPath")).read())
    if incident_instance["level"].upper() == "CRITICAL":
        new_incident_instance = {
            "appInstanceId": incident_instance.get("appInstanceId"),
            "appComponentInstanceId": incident_instance.get("appComponentInstanceId"),
            "cause": "异常状态POD数量超过[CRITICAL]告警等级容忍阈值,详情:" + incident_instance.get("content", "")
        }
        return new_incident_instance


def incident():
    new_incident_instance = build_new_instance()
    if new_incident_instance:
        # 自愈依赖新的异常实例
        result = {
            "nextIncident": new_incident_instance
        }
        open(os.getenv("varConfPath"), 'w').write(json.dumps(result))
        return result
    else:
        return {}


print(json.dumps(incident()))
